import * as React from 'react';
import { EditRowButton, DeleteRowButton } from './buttons';

export const ActionsColumn = ({
    mutationMode,
    noDelete,
    undoable,
    label,
    ...props
}: ActionsColumnProp) => (
    <>
        <EditRowButton />
        {!noDelete && (
            <DeleteRowButton
                mutationMode={mutationMode}
                undoable={undoable}
                {...props}
            />
        )}
    </>
);

export interface ActionsColumnProp {
    undoable?: boolean;
    noDelete?: boolean;
    redirect: string | boolean;
    [key: string]: any;
}
