import React from 'react';
import { useTranslate } from 'react-admin';
import { Tooltip, IconButton, IconButtonProps } from '@material-ui/core';
import CancelIcon from '@material-ui/icons/Cancel';

export const CancelEditButton = ({
    cancel,
    ...props
}: CancelEditButtonProps) => {
    const translate = useTranslate();
    return (
        <Tooltip
            title={translate('ra.action.cancel', {
                _: 'ra.action.cancel',
            })}
        >
            <IconButton onClick={cancel} size="small" {...props}>
                <CancelIcon />
            </IconButton>
        </Tooltip>
    );
};

export interface CancelEditButtonProps
    extends Omit<IconButtonProps, 'onClick'> {
    cancel: () => void;
}

export default CancelEditButton;
