import React, { ReactElement } from 'react';
import PropTypes from 'prop-types';
import { IconButton, Tooltip } from '@material-ui/core';
import ActionDelete from '@material-ui/icons/Delete';
import classnames from 'classnames';
import {
    useTranslate,
    Record,
    RedirectionSideEffect,
    useDeleteWithUndoController,
    useRecordContext,
    useResourceContext,
} from 'react-admin';

export const DeleteWithUndoIconButton = (
    props: DeleteWithUndoIconButtonProps
) => {
    const {
        basePath,
        className,
        label = 'ra.action.delete',
        redirect: redirectTo = 'list',
    } = props;
    const record = useRecordContext(props);
    const resource = useResourceContext(props);
    const { loading, handleDelete } = useDeleteWithUndoController({
        resource,
        record,
        redirect: redirectTo,
        basePath,
    });
    const translate = useTranslate();
    const translatedLabel = translate(label, { _: label });
    return (
        <Tooltip title={translatedLabel}>
            <IconButton
                aria-label={translatedLabel}
                disabled={loading}
                onClick={handleDelete}
                className={classnames('ra-delete-button', className)}
                key="button"
                size="small"
            >
                <ActionDelete color="error" />
            </IconButton>
        </Tooltip>
    );
};

export type DeleteWithUndoIconButtonProps = {
    basePath?: string;
    className?: string;
    confirmTitle?: string;
    confirmContent?: string;
    icon?: ReactElement;
    label?: string;
    onClick?: (e: MouseEvent) => void;
    record?: Record;
    redirect?: RedirectionSideEffect;
    resource?: string;
    invalid?: boolean;
    pristine?: boolean;
    saving?: boolean;
    submitOnEnter?: boolean;
};

DeleteWithUndoIconButton.propTypes = {
    basePath: PropTypes.string,
    className: PropTypes.string,
    confirmTitle: PropTypes.string,
    confirmContent: PropTypes.string,
    label: PropTypes.string,
    record: PropTypes.any,
    redirect: PropTypes.oneOfType([
        PropTypes.string,
        PropTypes.bool,
        PropTypes.func,
    ]),
    resource: PropTypes.string,
    icon: PropTypes.element,
};
