import * as React from 'react';
import { Tooltip, IconButton, IconButtonProps } from '@material-ui/core';
import ContentCreate from '@material-ui/icons/Create';
import { useTranslate } from 'react-admin';

import { useEditRowContext } from '../useEditRowContext';

export const EditRowButton = (props: EditRowButton) => {
    const { open } = useEditRowContext();
    const translate = useTranslate();

    return (
        <Tooltip title={translate('ra.action.edit', { _: 'ra.action.edit' })}>
            <IconButton
                onClick={open}
                size="small"
                color="primary"
                aria-label="edit"
                {...props}
            >
                <ContentCreate />
            </IconButton>
        </Tooltip>
    );
};

export type EditRowButton = Omit<IconButtonProps, 'onClick'>;
