export interface PreferencesDataProvider<
    PreferencesType extends unknown = any
> {
    getPreferences: () => Promise<PreferencesResult<PreferencesType>>;
    setPreferences: (
        params: SetPreferencesParams<PreferencesType>
    ) => Promise<PreferencesResult<PreferencesType>>;
}

export type PreferencesResult<PreferencesType extends unknown = any> = {
    data: PreferencesType;
};

export type SetPreferencesParams<PreferencesType extends unknown = any> = {
    data: PreferencesType;
    previousData: PreferencesType;
};
